'use strict';

var forms = require('forms');

var fields = forms.fields;
var validators = forms.validators;

/**
 * A user login form.
 *
 * @property loginForm
 */
module.exports = forms.create({
  login: fields.string({ required: validators.required('Por favor ingresa tu Email') }),
  password: fields.password({ required: validators.required('Por favor ingresa tu Password') }),
  idEmpresa: fields.string({ required: validators.required('Por favor ingresa tu Empresa') })
});
